package func;import entity.User;import entity.UserXML;import utils.FileUtils;import java.util.ArrayList;import java.util.List;public class UserFunc {    private static final String USER_FILE_NAME = "user.xml";    List<User> users = readLisUsers();    public boolean checkUser(User user) {        users = readLisUsers();        for (User u : users) {            if (user != null) {                if (u.getUserName().equals(user.getUserName()) && u.getPassword().equals(user.getPassword())) {                    return true;                }            }        }        return false;    }    public boolean checkUserName(User user) {        if (user.getUserName().equals("") || user.getPassword().equals("")){            return false;        }        for (User u : users) {            if (user != null) {                if (u.getUserName().equals(user.getUserName())) {                    return false;                }            }        }        return true;    }    public void add(User user) {        users.add(user);        writeListStudents(users);    }    public void writeListStudents(List<User> users) {        UserXML userXML = new UserXML();        userXML.setStudent(users);        FileUtils.writeXMLtoFile(USER_FILE_NAME, userXML);    }    public List<User> readLisUsers() {        List<User> list = new ArrayList<User>();        UserXML userXML = (UserXML) FileUtils.readXMLFile(                USER_FILE_NAME, UserXML.class);        if (userXML != null) {            list = userXML.getStudent();        }        return list;    }}